$(document).ready(function() {
  // If user clicks the 'Answered' button
  $(document).on('click', '#markAnsweredBtn', function() {
    const tid = ajaxify.data.tid;  // Current topic ID
    $.ajax({
      url: `${config.relative_path}/api/topic/${tid}/answer-status`,
      type: 'PUT',
      data: { answered: true },
      success: function(response) {
        // Optionally update UI or show success message
        console.log('Topic marked as answered', response);
      },
      error: function(err) {
        console.error('Failed to mark answered:', err);
      }
    });
  });

  // If user clicks the 'Unanswered' button
  $(document).on('click', '#markUnansweredBtn', function() {
    const tid = ajaxify.data.tid;
    $.ajax({
      url: `${config.relative_path}/api/topic/${tid}/answer-status`,
      type: 'PUT',
      data: { answered: false },
      success: function(response) {
        console.log('Topic marked as unanswered', response);
      },
      error: function(err) {
        console.error('Failed to mark unanswered:', err);
      }
    });
  });
});
